"""Separate module to handle framework test data"""
from attrdict import AttrDict


def load_test_data():
    test_data_dict = {
        'STEP_PAYMENT': AttrDict({"CARDINAL_URL": "https://merchant.example.com:8443/?myBillAmount=&myBillName=&myBillEmail=&myBillTel=&errorcode=0&transactionreference=42-14-119&baseamount=1000&currencyiso3a=GBP&errormessage=Payment+has+been+successfully+processed&settlestatus=0&jwt=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9._.ypZyN2CMwn9q4niUeERIgHDR2ISQAtp0otpCXIUPoow",
                                "VISA_URL": "https://merchant.example.com:8443/?myBillAmount=&myBillName=&myBillEmail=&myBillTel=&errorcode=0&baseamount=1099&transactionreference=1-2-345&orderreference=MyOrder123&currencyiso3a=GBP&errormessage=Payment+has+been+successfully+processed&settlestatus=0&jwt=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..nNZGIc8rN5ZnuAgzJs2-oDGPSdbeEMQXmFQlD-S_2yE"})
    }

    return AttrDict(test_data_dict)


TEST_DATA = load_test_data()


class TestData:

    def __init__(self, config__test):
        self._base_page = config__test.base_page

        """ User section """
        self.step_payment_cardinal_url = TEST_DATA.STEP_PAYMENT.CARDINAL_URL
        self.step_payment_visa_url = TEST_DATA.STEP_PAYMENT.VISA_URL
